greatestProduct = 0
thousandDigitNumber = ""
thousandDigitNumberString = str(thousandDigitNumber)
thousandDigitNumberList = []
for i in thousandDigitNumberString:
    thousandDigitNumberList.append(int(i))

for j in range(len(thousandDigitNumberList)-1):
    try:
        if(thousandDigitNumberList[j] * thousandDigitNumberList[j+1] * thousandDigitNumberList[j+2] * thousandDigitNumberList[j+3] * thousandDigitNumberList[j+4] * thousandDigitNumberList[j+5] * thousandDigitNumberList[j+6] * thousandDigitNumberList[j+7] * thousandDigitNumberList[j+8] * thousandDigitNumberList[j+9] * thousandDigitNumberList[j+10] * thousandDigitNumberList[j+11] * thousandDigitNumberList[j+12] > greatestProduct):
            greatestProduct = thousandDigitNumberList[j] * thousandDigitNumberList[j+1] * thousandDigitNumberList[j+2] * thousandDigitNumberList[j+3] * thousandDigitNumberList[j+4] * thousandDigitNumberList[j+5] * thousandDigitNumberList[j+6] * thousandDigitNumberList[j+7] * thousandDigitNumberList[j+8] * thousandDigitNumberList[j+9] * thousandDigitNumberList[j+10] * thousandDigitNumberList[j+11] * thousandDigitNumberList[j+12] 
    except IndexError:
        pass
print(greatestProduct)
